using System;
using System.Text;
using System.Collections;
using System.Collections.Generic;
using System.Text.RegularExpressions;
using System.Runtime.InteropServices;
using System.IO;
using System.Windows.Forms;
using System.Drawing;
using System.Drawing.Drawing2D;
using System.Drawing.Imaging;

class OTS_CLIPBOARD
{
	private Dictionary<int, Tuple<string,string, string>> m_rows = new Dictionary<int, Tuple<string,string, string>>();
	private IDictionaryEnumerator m_rows_enumerator = null;

	public void Clear()
	{
		Clipboard.SetDataObject(string.Empty, true); 
	}
	public void PutText(String txt)
	{
		Clipboard.SetDataObject(txt, true); 
	}
	public String GetText()
	{
		if(Clipboard.GetDataObject() !=null && Clipboard.GetDataObject().GetDataPresent(DataFormats.UnicodeText))
			return Clipboard.GetDataObject().GetData(DataFormats.UnicodeText).ToString();
		return GetAnsiText();
	}
	public String GetAnsiText()
	{
		if(Clipboard.GetDataObject() !=null && Clipboard.GetDataObject().GetDataPresent(DataFormats.Text)) 
			return Clipboard.GetDataObject().GetData(DataFormats.Text).ToString();
		return string.Empty;
	}
	public void SetFilePaths(Object[] args)
	{
		if(args==null) return;
		string[] filepaths = new string[args.Length];
		for(int i=0; i<args.Length; i++)
		{
			filepaths[i] = (string)args[i];
		}
		Clipboard.SetData(DataFormats.FileDrop, filepaths);
	}
	public String GetFilePaths(Object[] args)
	{
		string result = string.Empty;
		Regex re = new Regex(args!=null && args.Length>1? (string)args[1] : ".*");
		string separator = args!=null && args.Length>0? (string)args[0] : ";";
		int nth_occurence;
		if(int.TryParse(separator, out nth_occurence))
		{
			if(nth_occurence > 0)
			{
				separator = string.Empty;
			}
		}
		else
		{
			nth_occurence = -1;
		}
		string sep = string.Empty;
		if(Clipboard.GetDataObject() !=null && Clipboard.GetDataObject().GetDataPresent(DataFormats.FileDrop))
		{
			string[] filepaths = (string[])Clipboard.GetData(DataFormats.FileDrop);
			int occurence = 1;
			foreach(string filepath in filepaths)
			{
				if(re.Match(filepath).Success)
				{
					if(nth_occurence < 0 || occurence == nth_occurence)
					{
						result += sep + filepath;
						sep = separator;
					}
					occurence++;
				}
			}
		}
		return result;
	}
	/*
		imgdate can be a filepath to an image or a base64 encoded  string that contains a pnng image
	*/
	public void SetImage(string imgdata)
	{
		if(string.IsNullOrEmpty(imgdata)) return;
		if(imgdata.Length < 255 && File.Exists(imgdata)) // assume filepath
		{
			Image ti = Image.FromFile(imgdata);
			using (MemoryStream ms = new MemoryStream())
			{
				ti.Save(ms, ImageFormat.Bmp);
				Bitmap bm = new Bitmap(ms);
				Clipboard.SetImage(bm);
			}
			return;
		}
		byte[] bbfr = Convert.FromBase64String(imgdata);
		using (MemoryStream ms = new MemoryStream(bbfr))
		{
			ms.Position = 0;
			Bitmap bm = (Bitmap)Bitmap.FromStream(ms);
			Clipboard.SetImage(bm);	
		}
	}
	/*
		returns an image on the clipboard as  base64 encoded JPG compressed image if called without arguments or first argument empty
		the optional second defines compression, default is 30. Lower values is more compressed, less quality
	*/
	public string GetJPGImage(Object[] args)
	{
		string filepath = args==null || args.Length<1 ? null : (string)args[0];
		long compress = args!=null && args.Length > 1 ? long.Parse((string)args[1]) : 30L;		
		if (Clipboard.ContainsImage())
		{
			Image img = Clipboard.GetImage();
			return Image2CompressedBase64(img, compress, filepath);
		} 
		return string.Empty;
	}
	/*
		returns the clipboard as base64 encoded PNG image prepended with data:image/png;base64,
		the result can be used in html img elements as inline src value: <img src="">
	*/
	public string GetPNGImageSrc()
	{
		StringBuilder bfr = new StringBuilder("data:image/png;base64,");
		bfr.Append( GetPNGImage(null) );
		return bfr.ToString();
	}
	/*
		returns the clipboard as base64 encoded PNG
	*/
	public string GetPNGImage(Object[] args)
	{
		string filepath = args==null || args.Length<1 ? null : (string)args[0];
		if (Clipboard.ContainsImage())
		{
			Image img = Clipboard.GetImage();
			if(string.IsNullOrEmpty(filepath))
			{
				using (MemoryStream ms = new MemoryStream())
				{
					img.Save(ms, ImageFormat.Png);
					return System.Convert.ToBase64String(ms.ToArray());
				}
			}
			else
			{
				img.Save(filepath, ImageFormat.Png);
				return filepath;
			}
		}
		return string.Empty;
	}
	/*
		returns the clipboard as base64 encoded JPG image prepended with data:image/jpg;base64,
		the result can be used in html img elements as inline src value: <img src="">
	*/
	public string GetJPGImageSrc(long? compress)
	{
		StringBuilder bfr = new StringBuilder("data:image/jpg;base64,");
		bfr.Append( GetJPGImage( compress==null ? null : (new Object[] { null, compress.Value.ToString() } ) ) );
		return bfr.ToString();
	}
	/*
		Utility to convert a file with an image 9png,bmp,..)  into a compressed JPG format.
		returns base64 encoded jpg image if called without arguments or first argument empty
		Optionally, writes to an outut file specified as the first argument
		Optionally compresses by the value passed in a second argument.
	*/
	public string Image2JPG(string filepath, Object[] args)
	{
		string outfilepath = args==null || args.Length<1 ? null : (string)args[0];
		long compress = args!=null && args.Length > 1 ? long.Parse((string)args[1]) : 30L;
		Image ti = Image.FromFile(filepath);
		return Image2CompressedBase64(ti, compress, outfilepath);
	}
	public string Image2PNG(string filepath, Object[] args)
	{
		string outfilepath = args==null || args.Length<1 ? null : (string)args[0];
		Image ti = Image.FromFile(filepath);
		return Image2PNGBase64(ti, outfilepath);
	}
	/*
		audio data must be in wav format
	*/
	public void SetAudio(string audiodata)
	{
		if(string.IsNullOrEmpty(audiodata)) return;
		if(audiodata.Length < 255 && File.Exists(audiodata)) // assume filepath
		{
			byte[] audio = File.ReadAllBytes(audiodata);
			Clipboard.SetAudio(audio);
			return;
		}
		byte[] bbfr = Convert.FromBase64String(audiodata);
		Clipboard.SetAudio(bbfr);
	}
	public string GetAudioSrc()
	{
		StringBuilder bfr = new StringBuilder("data:audio/mp3;base64,");
		bfr.Append( GetAudio(null) );
		return bfr.ToString();
	}
	public string GetAudio(Object[] args)
	{
		string filepath = args==null || args.Length<1 ? null : (string)args[0];
		if(Clipboard.ContainsAudio())
		{
			Stream audio = Clipboard.GetAudioStream();
			if(!string.IsNullOrEmpty(filepath))
			{
				using (Stream fs = File.Create(filepath))
				{
					audio.CopyTo(fs);
				}
				return filepath;
			}
			else
			{
				Byte[] bytes;
				using (var ms = new MemoryStream())
				{
					Clipboard.GetAudioStream().CopyTo(ms);
					bytes = ms.ToArray();
				}
				return Convert.ToBase64String(bytes);
			}
		}
		return string.Empty;
	}
	public int QueryExe(string key, string type, string txt, string base64data)  
	{
		string resulttype;
		int keyseqno = 1;
		IDataObject clips = Clipboard.GetDataObject();
		m_rows.Clear();
		bool hastext = false;
		if( (string.IsNullOrEmpty(type) || DataFormats.UnicodeText.ToString().Equals(type)) && clips.GetDataPresent(DataFormats.UnicodeText))
		{
			resulttype = DataFormats.UnicodeText.ToString();
			m_rows.Add( 
				keyseqno++,
				Tuple.Create(resulttype, Clipboard.GetDataObject().GetData(DataFormats.UnicodeText).ToString(), string.Empty )
			);
			hastext = true;
		}
		if(!hastext && (string.IsNullOrEmpty(type) || DataFormats.Text.ToString().Equals(type)) && clips.GetDataPresent(DataFormats.Text))
		{
			resulttype = DataFormats.Text.ToString();
			m_rows.Add(
				keyseqno++,
				Tuple.Create(resulttype, Clipboard.GetDataObject().GetData(DataFormats.Text).ToString(), string.Empty) 
			);
			hastext = true;
		}
		if(!hastext && (string.IsNullOrEmpty(type) || DataFormats.StringFormat.ToString().Equals(type)) && clips.GetDataPresent(DataFormats.StringFormat))
		{
			resulttype = DataFormats.StringFormat.ToString();
			m_rows.Add( 
				keyseqno++,
				Tuple.Create(resulttype, Clipboard.GetDataObject().GetData(DataFormats.StringFormat).ToString(), string.Empty ) 
			);
			hastext = true;
		}
		if((string.IsNullOrEmpty(type) || DataFormats.FileDrop.ToString().Equals(type)) && clips.GetDataPresent(DataFormats.FileDrop))
		{
			resulttype = DataFormats.FileDrop.ToString();
			string[] filepaths = (string[])Clipboard.GetData(DataFormats.FileDrop);
			foreach(string filepath in filepaths)
			{
				m_rows.Add(
					keyseqno++,
					Tuple.Create(resulttype, filepath, string.Empty) 
				);
			}		
		}
		if (Clipboard.ContainsImage())
		{
			Image img = Clipboard.GetImage();
			resulttype = img.GetType().Name;
			m_rows.Add( 
				keyseqno++,
				Tuple.Create(resulttype, string.Empty, Image2PNGBase64(img, null) ) 
			);
		}
		if (Clipboard.ContainsAudio())
		{
			byte[] bytes;
			using (var ms = new MemoryStream())
			{
				Clipboard.GetAudioStream().CopyTo(ms);
				bytes = ms.ToArray();
			}
			resulttype = "WaveAudio";
			m_rows.Add( 
				keyseqno++,
				Tuple.Create(resulttype, string.Empty, Convert.ToBase64String(bytes) ) 
			);
		}
		m_rows_enumerator = m_rows.GetEnumerator();	
		return m_rows.Count > 0? 1:0;
	}
	private string Image2PNGBase64(Image im, string outfilepath)
	{
		if(string.IsNullOrEmpty(outfilepath))
		{
			using (var ms = new MemoryStream())
			{    
				im.Save(ms, ImageFormat.Png);
				return Convert.ToBase64String(ms.GetBuffer());
			}
		}
		else
		{
			im.Save(outfilepath, ImageFormat.Png);
		}
		return string.Empty;
	}
	private static string Image2CompressedBase64(Image img, long compress, string filepath)
	{	
		MemoryStream ms = new MemoryStream();
		img.Save(ms, ImageFormat.Bmp);
		Bitmap bm = new Bitmap(ms);
		ImageCodecInfo jpgEncoder = GetEncoderInfo(ImageFormat.Jpeg);
        EncoderParameters encParams = getEncodingParams(compress);
		if(string.IsNullOrEmpty(filepath))
		{
			using (MemoryStream msjpg = new MemoryStream())
			{
				bm.Save(msjpg, jpgEncoder, encParams);
				string base64 = System.Convert.ToBase64String(msjpg.ToArray());
				return base64;
			}
		}
		else
		{
			bm.Save(filepath, jpgEncoder, encParams);
			return filepath;
		}
	}
	private static ImageCodecInfo GetEncoderInfo(ImageFormat format)
	{
		ImageCodecInfo[] codecs = ImageCodecInfo.GetImageDecoders();
		foreach (ImageCodecInfo codec in codecs)
		{
			if (codec.FormatID == format.Guid)
			{
				return codec;
			}
		}
		return null;
	}
	private static EncoderParameters getEncodingParams(long compress)
	{
		System.Drawing.Imaging.Encoder enc = System.Drawing.Imaging.Encoder.Quality;
        EncoderParameters encPars = new EncoderParameters(1);
        encPars.Param[0] = new EncoderParameter(enc, compress);
		return encPars;
	}
	public int QueryFetch(out int key, out string type, out string txtdata, out string base64data )
	{
		key = 0;
		type = txtdata = base64data = string.Empty;
		if(!m_rows_enumerator.MoveNext())
		{
			return 0;
		}
		key = (int)m_rows_enumerator.Key;
		Tuple<string,string,string> t = (Tuple<string,string,string>)m_rows_enumerator.Value;
		type = t.Item1;
		txtdata = t.Item2;
		base64data = t.Item3;
		return 1;
	}
	public int DeleteRow(int key)
	{
		m_rows.Remove(key);
		return 1;
	}
	public int UpdateRow(int key, string type, string txtdata, string base64data)
	{
		m_rows.Remove(key);
		m_rows.Add(key, Tuple.Create<string,string,string>(type, txtdata, base64data));
		return 1;
	}
	public int InsertRow(int key, string type, string txtdata, string base64data)
	{
		type = string.IsNullOrEmpty(type)?"UnicodeText":type;
		m_rows.Add( key, Tuple.Create<string,string,string>(type, txtdata, base64data ));
		return 1;
	}
	public void Rollback()
	{
	}
	public int Commit()
	{
		if(m_rows.Count == 0)
		{
			Clipboard.Clear();
			return 1;
		}
		bool hasfiledrops = false;
		List<string> filedrops = new List<string>();
		IDataObject clips = new DataObject();
		bool audio_image = false;
		foreach(KeyValuePair<int,Tuple<string,string,string>> t in m_rows)
		{
			switch(t.Value.Item1)
			{
				case "Text": 
					clips.SetData(DataFormats.Text, t.Value.Item2); 
				break;
				case "UnicodeText": 
					clips.SetData(DataFormats.UnicodeText,  t.Value.Item2); 
				break;
				case "System.String":
					clips.SetData(DataFormats.StringFormat,  t.Value.Item2);
				break;
				case "FileDrop":
					hasfiledrops = true;
					filedrops.Add(t.Value.Item2);					
				break;
				case "Bitmap":
				{
					byte[] bbfr = Convert.FromBase64String(t.Value.Item3);
					using(MemoryStream ms = new MemoryStream(bbfr))
					{
						ms.Position = 0;
						Image im = Image.FromStream(ms);
						Bitmap bm = new Bitmap(im);
						Clipboard.SetImage(bm);
						audio_image = true;
					}
				}
				break;
				case "WaveAudio":
				{
					byte[] bbfr = Convert.FromBase64String(t.Value.Item3);
					using(MemoryStream ms = new MemoryStream(bbfr))
					{
						ms.Position = 0;
						Clipboard.SetAudio(bbfr);
						audio_image = true;
					}
				}
				break;
				default:
					throw new Exception("Clipboard type '" + t.Value.Item1 + "' is not supported");
				break;
			}
		}
		if(hasfiledrops)
		{
			string[] filepaths = filedrops.ToArray();
			clips.SetData(DataFormats.FileDrop, filepaths);
		}
		if(!audio_image) Clipboard.SetDataObject(clips, true);
		return 1;
	}
}
