T_RDMI_DOTNET_COMPONENT.PROGRAM_SOURCE:
using System;
using System.Windows.Forms;

class OTS_FILESYSTEM_DIALOGS 
{
	public static String Files(string idir, string title, string filter)
	{
		OpenFileDialog dlg = new OpenFileDialog();
		if (idir!=null)
			dlg.InitialDirectory = idir.Substring(0, idir.LastIndexOf(@"\"));
		dlg.Title = title;
		dlg.Filter = filter; // "Rich Text Files (*.txt)|*.TXT|" + "All files (*.*)|*.*";

		if (dlg.ShowDialog() == DialogResult.OK)
		   return dlg.FileName;  
		return string.Empty;
	}
	public static String Folders(string idir, string description)
	{
		FolderBrowserDialog dlg = new FolderBrowserDialog();
		if (idir!=null)
			dlg.SelectedPath = idir;
		dlg.Description = description;

		if (dlg.ShowDialog() == DialogResult.OK)
			return dlg.SelectedPath;  
		return idir;
	}
}
